// Process customer data for churn analysis
const customerData = $json.customers;
const processedCustomers = [];

for (const customer of customerData) {
  // Calculate risk factors
  const riskFactors = [];
  let riskScore = 0;

  // Age-based risk
  if (customer.age > 65) {
    riskFactors.push('Senior customer - higher churn potential');
    riskScore += 0.1;
  }

  // Income-based risk  
  if (customer.income < 40000) {
    riskFactors.push('Lower income bracket');
    riskScore += 0.15;
  }

  // Tenure-based risk
  if (customer.months_as_customer < 12) {
    riskFactors.push('New customer - relationship developing');
    riskScore += 0.2;
  }

  // Satisfaction-based risk
  if (customer.satisfaction_score < 6) {
    riskFactors.push('Below average satisfaction');
    riskScore += 0.3;
  }

  // Lifetime value assessment
  let valueSegment = 'LOW';
  if (customer.lifetime_value > 3000) valueSegment = 'HIGH';
  else if (customer.lifetime_value > 1500) valueSegment = 'MEDIUM';

  // Generate business recommendation
  let recommendation = 'MONITOR';
  let priority = 'LOW';

  if (riskScore > 0.4 && valueSegment === 'HIGH') {
    recommendation = 'IMMEDIATE_INTERVENTION';
    priority = 'CRITICAL';
  } else if (riskScore > 0.3) {
    recommendation = 'PROACTIVE_ENGAGEMENT';
    priority = 'HIGH';
  } else if (riskScore > 0.2) {
    recommendation = 'RETENTION_CAMPAIGN';
    priority = 'MEDIUM';
  }

  processedCustomers.push({
    customer_id: customer.customer_id,
    age: customer.age,
    income: customer.income,
    months_as_customer: customer.months_as_customer,
    satisfaction_score: customer.satisfaction_score,
    lifetime_value: customer.lifetime_value,
    risk_score: riskScore,
    risk_factors: riskFactors,
    value_segment: valueSegment,
    recommendation: recommendation,
    priority: priority,
    analysis_timestamp: new Date().toISOString()
  });
}

return processedCustomers.map(customer => ({ json: customer }));

// Generate churn prediction using DEBT API
const customer = $json;
const churnPredictionData = {
  age: customer.age,
  income: customer.income,
  credit_score: 650 + (customer.income / 1000), // Estimated credit score
  months_as_customer: customer.months_as_customer,
  num_products: Math.ceil(customer.lifetime_value / 500), // Estimate products
  monthly_charges: customer.lifetime_value / customer.months_as_customer,
  satisfaction_score: customer.satisfaction_score,
  support_tickets: customer.risk_score > 0.3 ? 3 : 1 // Estimate support tickets
};

return [{ 
  json: {
    customer_data: customer,
    prediction_input: churnPredictionData
  }
}];

// Generate comprehensive retention campaign
const customerData = $input.first().json.customer_data;
const mlPrediction = $input.last().json;

// Combine rule-based and ML predictions
const finalRiskScore = (customerData.risk_score + mlPrediction.prediction) / 2;
const confidence = mlPrediction.confidence || 0.8;

// Generate personalized retention strategy
let retentionStrategy = [];
let campaignType = 'STANDARD';
let estimatedCost = 50;
let expectedROI = 2.0;

if (finalRiskScore > 0.7) {
  campaignType = 'PREMIUM_INTERVENTION';
  estimatedCost = 200;
  expectedROI = 3.5;
  retentionStrategy = [
    'Assign dedicated account manager',
    'Offer 20% discount for next 3 months',
    'Provide premium support access',
    'Schedule executive review call',
    'Implement loyalty rewards program'
  ];
} else if (finalRiskScore > 0.4) {
  campaignType = 'PROACTIVE_ENGAGEMENT';
  estimatedCost = 75;
  expectedROI = 2.8;
  retentionStrategy = [
    'Send personalized satisfaction survey',
    'Offer product upgrade discount',
    'Provide additional training/support',
    'Schedule check-in call'
  ];
} else {
  campaignType = 'PREVENTIVE_CARE';
  estimatedCost = 25;
  expectedROI = 2.2;
  retentionStrategy = [
    'Send appreciation message',
    'Provide usage tips and best practices',
    'Offer referral incentives'
  ];
}

// Calculate business impact
const potentialLoss = customerData.lifetime_value * finalRiskScore;
const campaignROI = (potentialLoss * (expectedROI - 1)) - estimatedCost;

const retentionCampaign = {
  customer_id: customerData.customer_id,
  campaign_type: campaignType,
  risk_score_final: finalRiskScore,
  ml_confidence: confidence,
  retention_strategy: retentionStrategy,
  estimated_cost: estimatedCost,
  potential_loss: potentialLoss,
  expected_roi: expectedROI,
  campaign_roi: campaignROI,
  priority: finalRiskScore > 0.7 ? 'CRITICAL' : finalRiskScore > 0.4 ? 'HIGH' : 'MEDIUM',
  created_timestamp: new Date().toISOString(),
  customer_profile: {
    value_segment: customerData.value_segment,
    satisfaction: customerData.satisfaction_score,
    tenure: customerData.months_as_customer,
    ltv: customerData.lifetime_value
  },
  ml_prediction: mlPrediction
};

return [{ json: retentionCampaign }];

// Create executive alert for high-value retention opportunities
