ðŸ“ˆ Expected ROI: ${(campaign.expected_roi * 100).toFixed(0)}%
ðŸ’¡ Campaign ROI: $${campaign.campaign_roi.toFixed(0)}

ðŸ”§ Retention Strategy:
${campaign.retention_strategy.map(s => `â€¢ ${s}`).join('\n')}

ðŸ“Š Customer Profile:
â€¢ Value Segment: ${campaign.customer_profile.value_segment}
â€¢ Satisfaction: ${campaign.customer_profile.satisfaction}/10
â€¢ Tenure: ${campaign.customer_profile.tenure} months

ðŸ• Generated: ${new Date().toLocaleString()}
`,
  data: campaign,
  timestamp: new Date().toISOString()
};

return [{ json: executiveAlert }];

// Aggregate customer analytics summary
const allCustomers = $input.all();
const totalCustomers = allCustomers.length;

// Calculate summary statistics
const criticalRisk = allCustomers.filter(c => c.json.priority === 'CRITICAL').length;
const highRisk = allCustomers.filter(c => c.json.priority === 'HIGH').length;
const mediumRisk = allCustomers.filter(c => c.json.priority === 'MEDIUM').length;
const lowRisk = allCustomers.filter(c => c.json.priority === 'LOW').length;

const avgRiskScore = allCustomers.reduce((sum, c) => sum + c.json.risk_score, 0) / totalCustomers;
const totalLifetimeValue = allCustomers.reduce((sum, c) => sum + c.json.lifetime_value, 0);
const avgSatisfaction = allCustomers.reduce((sum, c) => sum + c.json.satisfaction_score, 0) / totalCustomers;

// Risk distribution
const riskDistribution = {
  critical: (criticalRisk / totalCustomers * 100).toFixed(1),
  high: (highRisk / totalCustomers * 100).toFixed(1),
  medium: (mediumRisk / totalCustomers * 100).toFixed(1),
  low: (lowRisk / totalCustomers * 100).toFixed(1)
};

const analyticsSummary = {
  analysis_timestamp: new Date().toISOString(),
  total_customers_analyzed: totalCustomers,
  risk_summary: {
    critical_risk: criticalRisk,
    high_risk: highRisk,
    medium_risk: mediumRisk,
    low_risk: lowRisk
  },
  risk_distribution_percent: riskDistribution,
  business_metrics: {
    avg_risk_score: avgRiskScore.toFixed(3),
    total_lifetime_value: totalLifetimeValue,
    avg_satisfaction_score: avgSatisfaction.toFixed(2),
    customers_requiring_intervention: criticalRisk + highRisk
  },
  recommendations: [
    criticalRisk > 0 ? `${criticalRisk} customers need immediate intervention` : 'No critical risk customers',
    avgRiskScore > 0.3 ? 'Overall customer risk is elevated - review retention strategies' : 'Customer risk levels are manageable',
    avgSatisfaction < 7 ? 'Customer satisfaction below target - implement improvement programs' : 'Customer satisfaction levels are good'
  ]
};

console.log('DEBT Customer Analytics Summary:', analyticsSummary);
return [{ json: analyticsSummary }];
