// Define stock symbols for analysis
const symbols = ['AAPL', 'MSFT', 'GOOGL', 'TSLA', 'NVDA', 'SPY', 'QQQ'];
const outputs = [];

for (const symbol of symbols) {
  outputs.push({
    json: {
      symbol: symbol,
      timestamp: new Date().toISOString()
    }
  });
}

return outputs;

// Process Yahoo Finance data
const data = $input.all();
const processedData = [];

for (const item of data) {
  try {
    const result = item.json.chart.result[0];
    const symbol = result.meta.symbol;
    const currentPrice = result.meta.regularMarketPrice;
    const previousClose = result.meta.previousClose;
    const change = currentPrice - previousClose;
    const changePercent = (change / previousClose) * 100;

    // Get timestamp data
    const timestamps = result.timestamp;
    const prices = result.indicators.quote[0];

    // Calculate basic technical indicators
    const closes = prices.close.filter(p => p !== null);
    const volumes = prices.volume.filter(v => v !== null);

    // Simple moving average (last 20 periods)
    const sma20 = closes.length >= 20 ? 
      closes.slice(-20).reduce((a, b) => a + b) / 20 : null;

    // RSI calculation (simplified)
    let rsi = 50; // Default neutral
    if (closes.length >= 14) {
      const gains = [];
      const losses = [];

      for (let i = 1; i < closes.length && i <= 14; i++) {
        const diff = closes[closes.length - i] - closes[closes.length - i - 1];
        if (diff > 0) gains.push(diff);
        else losses.push(Math.abs(diff));
      }

      const avgGain = gains.length > 0 ? gains.reduce((a, b) => a + b) / gains.length : 0;
      const avgLoss = losses.length > 0 ? losses.reduce((a, b) => a + b) / losses.length : 0;

      if (avgLoss !== 0) {
        const rs = avgGain / avgLoss;
        rsi = 100 - (100 / (1 + rs));
      }
    }

    // Generate trading signal
    let signal = 'HOLD';
    let signalStrength = 0;

    if (changePercent > 2 && rsi < 70) {
      signal = 'STRONG_BUY';
      signalStrength = 0.8;
    } else if (changePercent > 0.5 && rsi < 70) {
      signal = 'BUY';
      signalStrength = 0.6;
    } else if (changePercent < -2 && rsi > 30) {
      signal = 'STRONG_SELL';
      signalStrength = -0.8;
    } else if (changePercent < -0.5 && rsi > 30) {
      signal = 'SELL';
      signalStrength = -0.6;
    }

    // Risk assessment
    let riskLevel = 'MEDIUM';
    if (Math.abs(changePercent) > 3) riskLevel = 'HIGH';
    else if (Math.abs(changePercent) < 1) riskLevel = 'LOW';

    processedData.push({
      json: {
        symbol: symbol,
        currentPrice: currentPrice,
        previousClose: previousClose,
        change: change,
        changePercent: changePercent,
        sma20: sma20,
        rsi: rsi,
        volume: volumes[volumes.length - 1],
        signal: signal,
        signalStrength: signalStrength,
        riskLevel: riskLevel,
        timestamp: new Date().toISOString(),
        marketCap: result.meta.marketCap || null,
        analysisDate: new Date().toISOString().split('T')[0]
      }
    });
  } catch (error) {
    console.error(`Error processing ${item.json.symbol}:`, error);
  }
}

return processedData;

