// Compare current KPIs with previous and calculate trends
const currentKPIs = $input.first().json;
const previousKPIs = $input.last().json;

// Default previous values if no historical data
const prev = previousKPIs.length > 0 ? previousKPIs[0] : {
  total_customers: currentKPIs.total_customers * 0.95,
  monthly_revenue: currentKPIs.monthly_revenue * 0.98,
  avg_customer_ltv: currentKPIs.avg_customer_ltv * 1.02,
  churn_rate: currentKPIs.churn_rate * 1.1,
  satisfaction_score: currentKPIs.satisfaction_score * 0.99
};

// Calculate trends and changes
const kpiAnalysis = {
  timestamp: new Date().toISOString(),
  current: currentKPIs,
  previous: prev,
  trends: {
    customer_growth: {
      absolute: currentKPIs.total_customers - prev.total_customers,
      percentage: ((currentKPIs.total_customers - prev.total_customers) / prev.total_customers * 100).toFixed(2)
    },
    revenue_growth: {
      absolute: currentKPIs.monthly_revenue - prev.monthly_revenue,
      percentage: ((currentKPIs.monthly_revenue - prev.monthly_revenue) / prev.monthly_revenue * 100).toFixed(2)
    },
    ltv_change: {
      absolute: currentKPIs.avg_customer_ltv - prev.avg_customer_ltv,
      percentage: ((currentKPIs.avg_customer_ltv - prev.avg_customer_ltv) / prev.avg_customer_ltv * 100).toFixed(2)
    },
    churn_change: {
      absolute: currentKPIs.churn_rate - prev.churn_rate,
      percentage: ((currentKPIs.churn_rate - prev.churn_rate) / prev.churn_rate * 100).toFixed(2)
    },
    satisfaction_change: {
      absolute: currentKPIs.satisfaction_score - prev.satisfaction_score,
      percentage: ((currentKPIs.satisfaction_score - prev.satisfaction_score) / prev.satisfaction_score * 100).toFixed(2)
    }
  }
};

// Business health assessment
let healthScore = 5; // Start with neutral
let healthFactors = [];

// Customer growth assessment
if (kpiAnalysis.trends.customer_growth.percentage > 5) {
  healthScore += 1;
  healthFactors.push('Strong customer growth');
} else if (kpiAnalysis.trends.customer_growth.percentage < -2) {
  healthScore -= 1;
  healthFactors.push('Customer growth declining');
}

// Revenue growth assessment
if (kpiAnalysis.trends.revenue_growth.percentage > 10) {
  healthScore += 2;
  healthFactors.push('Excellent revenue growth');
} else if (kpiAnalysis.trends.revenue_growth.percentage < -5) {
  healthScore -= 2;
  healthFactors.push('Revenue declining');
}

// Churn rate assessment
if (kpiAnalysis.trends.churn_change.percentage < -10) {
  healthScore += 1;
  healthFactors.push('Churn rate improving');
} else if (kpiAnalysis.trends.churn_change.percentage > 15) {
  healthScore -= 2;
  healthFactors.push('Churn rate worsening');
}

// Satisfaction assessment
if (kpiAnalysis.trends.satisfaction_change.percentage > 5) {
  healthScore += 1;
  healthFactors.push('Customer satisfaction improving');
} else if (kpiAnalysis.trends.satisfaction_change.percentage < -3) {
  healthScore -= 1;
  healthFactors.push('Customer satisfaction declining');
}

// Overall business health
let overallHealth = 'GOOD';
if (healthScore >= 8) overallHealth = 'EXCELLENT';
else if (healthScore >= 6) overallHealth = 'GOOD';
else if (healthScore >= 4) overallHealth = 'FAIR';
else overallHealth = 'POOR';

kpiAnalysis.business_health = {
  score: healthScore,
  rating: overallHealth,
  factors: healthFactors
};

// Generate alerts
const alerts = [];

// Revenue alert
if (parseFloat(kpiAnalysis.trends.revenue_growth.percentage) < -10) {
  alerts.push({
    type: 'REVENUE_DECLINE',
    severity: 'CRITICAL',
    message: `Revenue declined by ${Math.abs(kpiAnalysis.trends.revenue_growth.percentage)}%`,
    action_required: 'Immediate revenue recovery plan needed'
  });
}

// Churn alert
if (currentKPIs.churn_rate > 0.15) {
  alerts.push({
    type: 'HIGH_CHURN',
    severity: 'HIGH',
    message: `Churn rate is ${(currentKPIs.churn_rate * 100).toFixed(1)}% (above 15% threshold)`,
    action_required: 'Implement retention campaigns'
  });
}

// Customer growth alert
if (parseFloat(kpiAnalysis.trends.customer_growth.percentage) < -5) {
  alerts.push({
    type: 'CUSTOMER_DECLINE',
    severity: 'HIGH',
    message: `Customer base declined by ${Math.abs(kpiAnalysis.trends.customer_growth.percentage)}%`,
    action_required: 'Review acquisition strategies'
  });
}

// Satisfaction alert
if (currentKPIs.satisfaction_score < 6.5) {
  alerts.push({
    type: 'LOW_SATISFACTION',
    severity: 'MEDIUM',
    message: `Customer satisfaction at ${currentKPIs.satisfaction_score.toFixed(1)}/10`,
    action_required: 'Improve service quality'
  });
}

kpiAnalysis.alerts = alerts;

return [{ json: kpiAnalysis }];

// Generate comprehensive executive dashboard report
const kpiData = $json;

// Format trends for executive consumption
const formatTrend = (trend) => {
  const symbol = parseFloat(trend.percentage) >= 0 ? 'üìà' : 'üìâ';
  const color = parseFloat(trend.percentage) >= 0 ? 'positive' : 'negative';
  return `${symbol} ${trend.percentage}% (${trend.absolute > 0 ? '+' : ''}${typeof trend.absolute === 'number' ? trend.absolute.toLocaleString() : trend.absolute})`;
};

// Create executive summary
const executiveDashboard = {
